import React, { useState } from 'react';
import './css/LoginPage.css'; // Ïä§ÌÉÄÏùºÏùÑ ÏúÑÌïú CSS ÌååÏùº

const LoginPage = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:3001/api/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      if (response.ok) {
        onLogin(data.user); 
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <div className="login-page">
      <form onSubmit={handleSubmit}>
        <h1>üèÜAll-Rounder Login</h1>
        <div class="id-field">
          <label>All-Rounder ID</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div class="password-field">
          <label>All-Rounder ÎπÑÎ∞ÄÎ≤àÌò∏</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div class="login-addons">
          <div class="remember-me">
            <input type="checkbox" id="remember" name="remember" />
            <label for="remember">Remember me</label>
          </div>
          <div class="forgot-pwd">
            <label><div class="alter-page">ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûäÏúºÏÖ®ÎÇòÏöî?</div></label>
          </div>
        </div>

        <button type="submit" class="btn draw-border">Login</button>

        <div>
            <label class="sign-in-prompt">Í≥ÑÏ†ïÏù¥ ÏóÜÏúºÏã†Í∞ÄÏöî?&nbsp;<div class="alter-page">ÏÉà Í≥ÑÏ†ï ÎßåÎì§Í∏∞</div></label>
        </div>
      </form>
    </div>
  );
};

export default LoginPage;
